:root {
	/* Typography */
	--font-family: 'Open Sans', sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;

	--font-size-body: 15px;
	--font-size-label: 16px;
	--font-size-test-notice: 14px;
	--font-size-input: 16px;
	--font-size-header: 26px;
	--font-size-subheader: 18px;
	--font-size-paragraph: 16px;
	--font-size-link: 14px;
	--font-size-error: 14px;

	--font-size-html-h1: 34px;
	--font-size-html-h2: 26px;
	--font-size-html-h3: 20px;
	--font-size-html-h4: 18px;
	--font-size-html-h5: 16px;
	--font-size-html-h6: 14px;

	/* üåø Green Palette */
	--color-background: #f6faf8;

	--color-test-notice-text: #2f855a;
	--color-test-notice-bg: #edf7f2;
	--color-test-notice-border: #b7e4c7;

	--color-card-bg: #ffffff;
	--color-card-border: #d1e7dd;
	--color-card-shadow: rgba(16, 185, 129, 0.14);

	--color-header: #1f2937;
	--color-header-subtext: #6b7280;

	--color-label: #374151;
	--color-input-text: #1f2937;
	--color-input-border: #cfe5dc;
	--color-focus-border: #22c55e;

	--color-link: #16a34a;
	--color-html-link: #16a34a;
	--color-html-text: #374151;

	/* Primary Actions */
	--color-submit-btn-bg: #22c55e;
	--color-submit-btn-text: #ffffff;

	/* Errors */
	--color-error: #dc2626;
	--color-required: #22c55e;

	/* Secondary */
	--color-clear-button-bg: #9ca3af;

	/* Layout */
	--border-radius-card: 14px;
	--border-radius-input: 10px;
	--border-radius-clear-btn: 50%;

	/* Spacing */
	--padding-container-top: 40px;
	--padding-card: 32px;
	--padding-test-notice-vertical: 14px;
	--padding-test-notice-horizontal: 24px;
	--margin-bottom-card: 24px;
	--padding-form-input: 16px;

	/* Dimensions */
	--container-width: 560px;
	--submit-btn-height: 56px;
	--checkbox-size: 20px;

	/* Effects */
	--box-shadow-card: 0px 14px 40px rgba(16, 185, 129, 0.18);
	--opacity-placeholder: 0.45;
}

/* Apply to all n8n form pages */
.card {
	position: relative;
	max-width: 400px; /* smaller width */
	margin: 16px auto; /* reduced margin */
	padding: 16px; /* reduced padding */
	background: var(--color-card-bg, #ffffff) !important;
	border-radius: 12px;
	box-shadow: 0 6px 15px rgba(0, 0, 0, 0.1);
	font-family: var(--font-family, -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif);
	box-sizing: border-box;
}

/* Top accent bar */
.card:before {
	content: '';
	display: block;
	height: 40px; /* smaller top bar */
	background: linear-gradient(135deg, #16a34a 0%, #22c55e 100%);
	margin: -16px -16px 24px -16px; /* adjusted to match padding */
	border-radius: 12px 12px 0 0;
}

/* Headings */
.card h1,
.card h2 {
	text-align: center !important;
	color: var(--color-header, #111111) !important;
	font-weight: 600 !important;
	margin: 16px 0 !important;
	font-size: 20px !important; /* smaller font */
}

/* Paragraph text */
.card p {
	font-size: 14px !important; /* smaller font */
	line-height: 1.4 !important;
	color: var(--color-html-text, #333333);
}

/* Inputs */
.inputs-wrapper {
	position: relative;
}

.form-group {
	margin-bottom: 16px;
}

.card input,
.card textarea,
.card select {
	width: 100% !important;
	padding: 10px !important; /* reduced padding */
	border: 2px solid var(--color-input-border, #d1d5db) !important;
	border-radius: 8px !important;
	font-size: 14px !important; /* smaller font */
	color: var(--color-input-text, #111111);
	box-sizing: border-box !important;
	transition: border-color 0.25s ease, box-shadow 0.25s ease !important;
	background: #ffffff;
}

.card textarea {
	min-height: 100px; /* reduced height */
	resize: vertical;
}

.card input::placeholder,
.card textarea::placeholder {
	opacity: 0.5;
}

/* Focus state */
.card input:focus,
.card textarea:focus,
.card select:focus {
	border-color: #22c55e !important;
	box-shadow: 0 0 0 3px rgba(34, 197, 94, 0.15) !important;
	outline: none !important;
}

/* Submit button */
.card button[type="submit"] {
	background: linear-gradient(135deg, #16a34a 0%, #22c55e 100%) !important;
	color: #ffffff !important;
	padding: 12px 30px !important; /* smaller button */
	border-radius: 10px !important;
	font-size: 16px !important;
	font-weight: 600 !important;
	cursor: pointer !important;
	width: 100% !important;
	box-shadow: 0 4px 15px rgba(34, 197, 94, 0.35) !important;
	transition: transform 0.2s ease, box-shadow 0.2s ease !important;
	border: none !important;
}

.card button[type="submit"]:hover {
	transform: translateY(-1px) !important;
	box-shadow: 0 6px 20px rgba(34, 197, 94, 0.45) !important;
}

/* Error message */
.error-hidden {
	display: block;
	position: relative;
	color: var(--color-error, #dc2626);
	text-align: left;
	font-size: 12px;
	font-weight: 400;
	visibility: hidden;
	padding-top: 4px;
}

/* Responsive */
@media (max-width: 768px) {
	.card {
		margin: 12px !important;
		padding: 12px !important;
	}

	.card h1,
	.card h2 {
		font-size: 18px !important;
	}

	.card:before {
		margin: -12px -12px 20px -12px;
		height: 32px;
	}
}

