	--submit-btn-height: 56px;

	/* Effects */
	--box-shadow-card: 0px 14px 40px rgba(16, 185, 129, 0.18);
	--opacity-placeholder: 0.45;
}

/* Card */
.card {
	position: relative;
	max-width: var(--container-width);
	margin: 32px auto;
	padding: var(--padding-card);
	background: var(--color-card-bg) !important;
	border-radius: var(--border-radius-card);
	box-shadow: var(--box-shadow-card);
	font-family: var(--font-family);
}

/* Accent bar */
.card:before {
	content: '';
	display: block;
	height: 56px;
	background: linear-gradient(135deg, #16a34a 0%, #22c55e 100%);
	margin: calc(-1 * var(--padding-card))
	        calc(-1 * var(--padding-card))
	        32px
	        calc(-1 * var(--padding-card));
	border-radius: var(--border-radius-card) var(--border-radius-card) 0 0;
}

/* Headings */
.card h1,
.card h2 {
	text-align: center !important;
	color: var(--color-header) !important;
	font-weight: var(--font-weight-bold) !important;
	margin: 20px 0 !important;
	font-size: var(--font-size-header) !important;
}

/* Text */
.card p {
	font-size: var(--font-size-paragraph) !important;
	line-height: 1.6 !important;
	color: var(--color-html-text);
}

/* Inputs */
.form-group {
	margin-bottom: 20px;
}

.card input,
.card textarea,
.card select {
	width: 100% !important;
	padding: var(--padding-form-input) !important;
	border: 2px solid var(--color-input-border) !important;
	border-radius: var(--border-radius-input) !important;
	font-size: var(--font-size-input) !important;
	color: var(--color-input-text);
	transition: border-color 0.25s ease, box-shadow 0.25s ease !important;
}

.card textarea {
	min-height: 160px;
}

/* Focus */
.card input:focus,
.card textarea:focus,
.card select:focus {
	border-color: var(--color-focus-border) !important;
	box-shadow: 0 0 0 3px rgba(34, 197, 94, 0.15) !important;
	outline: none !important;
}

/* Submit */
.card button[type="submit"] {
	width: 100% !important;
	height: var(--submit-btn-height);
	background: linear-gradient(135deg, #22c55e 0%, #16a34a 100%) !important;
	color: var(--color-submit-btn-text) !important;
	border-radius: 12px !important;
	font-size: 18px !important;
	font-weight: var(--font-weight-bold) !important;
	box-shadow: 0 6px 20px rgba(34, 197, 94, 0.35) !important;
	border: none !important;
	cursor: pointer !important;
	transition: transform 0.2s ease, box-shadow 0.2s ease !important;
}

.card button[type="submit"]:hover {
	transform: translateY(-2px);
	box-shadow: 0 10px 28px rgba(34, 197, 94, 0.45) !important;
}

/* Error */
.error-hidden {
	color: var(--color-error);
	font-size: var(--font-size-error);
	visibility: hidden;
}

/* Mobile */
@media (max-width: 768px) {
	.card {
		margin: 16px !important;
		padding: 20px !important;
	}

	.card h1,
	.card h2 {
		font-size: 22px !important;
	}
}

